package com.test.basicToObject;

public class LongToObjectTest {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		long value = 4;
		Long object = (Long)value;
		Long   temp = null;
		System.out.println(value);
		System.out.println((Long)null);
		System.out.println((long)temp);
		System.out.println(object.intValue());

	}

}
